@import '../../../assets/scss/_variables';

.page-icons {
	height: 100%;
	margin: 0 !important;
	padding: 20px;
	padding-bottom: 10px;
	box-sizing: border-box;

	.search-wrap {
		margin-bottom: 15px;
		margin-top: 10px;

		i {
			display: inline-block;
			width: 22px;
		}

		input {
			outline: none;
			background: transparent;
			border: none;
			font-size: 15px;
			position: relative;
			top: -2px;
			width: 100%;
			padding: 0;
			color: $text-color;
		}

		.icons-tot {
			margin-right: 20px;
			margin-left: 10px;
		}

		a {
			border-bottom: 1px solid;
			text-decoration: none;
			color: $text-color;

			&:hover {
				opacity: .6;
			}
		}
	}

	.icons-list {
		//background: #fff;
		//margin-bottom: 10px;
		padding: 8px;
		box-sizing: border-box;

		.icon {
			width: 150px;
			height: 150px;
			text-align: center;
			display: block;
			float: left;
			line-height: 150px;
			margin: 7px;
			overflow: hidden;
			box-sizing: border-box;
			box-shadow: none;
			border-radius: 20px;
			padding: 8px;
			transition: all .5s;

			i {
				font-size: 40px;
			}
			.title {
				font-size: 14px;
				line-height: 13px;
				padding: 16px 0;
			}
			.class {
				font-size: 12px;
				line-height: 13px;
				opacity: .5;
			}

			&:hover {
				line-height: 23px;
				padding-top: 25px;
				background: transparentize($background-color, 0.7);
				box-shadow: 0 2px 4px 0 rgba(50,50,93,.1);
			}
		}
	}
}

@media (max-width: 768px) {
	.page-icons {
		overflow-x: hidden !important;
		overflow-y: auto !important;
		flex: none !important;
		display: block !important;
		width: 100%;
		
		.icons-list {
			.icon {
				width: 125px;
				height: 125px;
				line-height: 125px;
				margin: 3px;
				padding: 4px;

				i {
					font-size: 30px;
				}
				.title {
					font-size: 12px;
					line-height: 12px;
					padding: 12px 0;
				}
				.class {
					font-size: 10px;
					line-height: 10px;
					opacity: .5;
				}
			}
		}
	}
}
