import moment from "moment";
import Affix from "@/components/Affix";
import {
    mapGetters,
    mapActions
} from "vuex";
import * as getter from "../../../store/getters-types";
import * as action from "../../../store/action-types";
import * as mutation from '../../../store/mutation-types'
import swal from 'sweetalert2'

export default {
    name: "Attendance",
    data() {
        return {
            dialogVisible: false,
            currentPage: 1,
            input: "",
            affixEnabled: true,
            indexT: "",
            userid: null,
            date: null,
            radio: null,
            month: null,
            tableData: null,
            totalData: null,
            mapMark: null,
            markers: [],
            mapLine: null,
            path: [],
            markDefault: {
                lat: -6.21462,
                lng: 106.84513
            },
            resultTrack: null,
            image_url: null,
            info_marker: null,
            infowindow: {
                lat: 0,
                lng: 0,
                name: null,
            },
            orangeIcon: require("@/assets/images/orange-dot.png"),
            redIcon: require("@/assets/images/red-dot.png"),
            blueDot: require("@/assets/images/blue-dot.png"),
            window_open: false
        };
    },
    components: {
        Affix
    },
    computed: {
        ...mapGetters({
            listData: getter.GET_LIST_ATT,
            paginate: getter.GET_PAGINATE_ATT,
            dropDown: getter.GET_DROPDOWN_EMPLOYEE,
            filterAttendance: getter.GET_FILTER_ATT,
            attTotal: getter.GET_ATT_TOTAL,
            filterTotal: getter.GET_FILTER_TOTAL,
            button: getter.GET_BUTTON,
            loading: getter.GET_LOADING,
            attendance: getter.GET_TRACKING_ATTENDANCE
        }),
        paginator() {
            return this.paginate;
        },
        indexTam() {
            return this.indexT;
        },
        disableRadio1() {
            if (this.radio == 2) {
                return true;
            } else if (this.radio == 1) {
                return false;
            } else {
                return true;
            }
        },
        disableRadio2() {
            if (this.radio == 1) {
                return true;
            } else if (this.radio == 2) {
                return false;
            } else {
                return true;
            }
        },
        dateExport() {
            if (this.date) {
                const foo = {
                    startdate: this.date[0],
                    duedate: this.date[1],
                }
                return foo

            } else if (this.month == 99) {
                const foo = {
                    startdate: this.forToday,
                    duedate: this.forToday,

                }
                return foo
            } else {
                return null;
            }
        },
        forToday() {
            let today = new Date();
            let dd = String(today.getDate()).padStart(2, '0');
            let mm = String(today.getMonth() + 1).padStart(2, '0'); //January is 0!
            let yyyy = today.getFullYear();
            today = yyyy + '-' + mm + '-' + dd;
            return today;
        }
    },
    async mounted() {
        await this.$store.commit(mutation.BUTTON_STATUS, false);
        await this.$store.commit(mutation.SET_LOADING, true);
        await this.$store.dispatch(action.LIST_ATT_EMPLOYEE, this.currentPage);
        this.tableData = this.listData
        this.totalData = this.attTotal
        this.$store.dispatch(action.DROPDOWN_EMPLOYEE);
    },
    methods: {
        removeDate2() {
            this.month = null
        },
        removeDate() {
            this.date = null
        },
        openWindow(position) {
            this.window_open = true
            this.infowindow.lat = position.lat;
            this.infowindow.lng = position.lng;
            this.infowindow.name = position.name;
        },
        Dialog(foo, e) {
            e.stopPropagation();
            this.image_url = foo;
            this.dialogVisible = true;
        },
        async exportExcel() {
            let data = '';
            if (this.dateExport == null) {
                swal('Please select a start date', '', 'error')
            } else {
                if (this.userid) {
                    let obj = {
                        date: this.dateExport,
                        path: "employee",
                        userid: this.userid,
                    };
                    this.$store.commit(mutation.BUTTON_STATUS, true);
                    data = await this.$store.dispatch(action.EXPORT_ATTENDANCE, obj)
                    let firstD = data.slice(1)
                    let secondD = firstD.slice(0, firstD.length - 1)
                    window.open(secondD)
                } else if (this.userid == null | this.userid == 0) {
                    let obj = {
                        date: this.dateExport,
                        path: "employee",
                        userid: null,
                    };
                    this.$store.commit(mutation.BUTTON_STATUS, true);
                    data = await this.$store.dispatch(action.EXPORT_ATTENDANCE, obj)
                    let firstD = data.slice(1)
                    let secondD = firstD.slice(0, firstD.length - 1)
                    window.open(secondD)
                }
            }
        },
        async onFind() {
            if (this.userid == null) {
                this.currentPage = 1;
                await this.$store.commit(mutation.BUTTON_STATUS, true);
                await this.$store.dispatch(action.FILTER_EMPLOYEE, {
                    radio: this.radio,
                    iduser: 0,
                    today: this.forToday,
                    idmonth: this.month,
                    startdate: this.date ? this.date[0] : null,
                    duedate: this.date ? this.date[1] : null,
                    page: 1
                })
            } else {
                this.currentPage = 1;
                await this.$store.commit(mutation.BUTTON_STATUS, true);
                await this.$store.dispatch(action.FILTER_EMPLOYEE, {
                    radio: this.radio,
                    iduser: this.userid,
                    today: this.forToday,
                    idmonth: this.month,
                    startdate: this.date ? this.date[0] : null,
                    duedate: this.date ? this.date[1] : null,
                    page: 1
                })
            }
            this.tableData = this.filterAttendance;
            this.totalData = this.filterTotal;
            this.markers = [];
            this.path = [];
            this.window_open = false;
            for (var i = 0; i < this.tableData.length; i++) {
                const marker = {
                    lat: parseFloat(this.tableData[i].clock_in_latitude),
                    lng: parseFloat(this.tableData[i].clock_in_longitude),
                    name: this.tableData[i].name + ', ' + this.tableData[i].clock_in + ' (CLOCK IN)',
                    icon: this.redIcon
                };
                this.markDefault.lat = parseFloat(this.tableData[i].clock_in_latitude)
                this.markDefault.lng = parseFloat(this.tableData[i].clock_in_longitude)
                this.markers.push({
                    position: marker
                });
                this.mapMark = 1;
            }
            for (var i = 0; i < this.tableData.length; i++) {
                const marker = {
                    lat: parseFloat(this.tableData[i].clock_out_latitude),
                    lng: parseFloat(this.tableData[i].clock_out_longitude),
                    name: this.tableData[i].name + ', ' + this.tableData[i].clock_out + ' (CLOCK OUT)',
                    icon: this.orangeIcon
                };
                this.markers.push({
                    position: marker
                });
            }
        },
        async handlePageChange(currentpage) {
            this.currentPage = currentpage;
            if (this.userid == null) {
                await this.$store.commit(mutation.SET_LOADING, true);
                await this.$store.dispatch(action.FILTER_EMPLOYEE, {
                    radio: this.radio,
                    iduser: 0,
                    idmonth: this.month,
                    today: this.forToday,
                    startdate: this.date ? this.date[0] : null,
                    duedate: this.date ? this.date[1] : null,
                    page: this.currentPage
                });
            } else {
                await this.$store.commit(mutation.SET_LOADING, true);
                await this.$store.dispatch(action.FILTER_EMPLOYEE, {
                    radio: this.radio,
                    iduser: this.userid,
                    idmonth: this.month,
                    today: this.forToday,
                    startdate: this.date ? this.date[0] : null,
                    duedate: this.date ? this.date[1] : null,
                    page: this.currentPage
                });

            }
            this.tableData = this.filterAttendance;
            this.totalData = this.filterTotal;
            this.markers = [];
            this.path = [];
            this.window_open = false;
            for (var i = 0; i < this.tableData.length; i++) {

                const marker = {
                    lat: parseFloat(this.tableData[i].clock_in_latitude),
                    lng: parseFloat(this.tableData[i].clock_in_longitude),
                    name: this.tableData[i].name + ', ' + this.tableData[i].clock_in + ' (CLOCK IN)',
                    icon: this.redIcon
                };
                this.markDefault.lat = parseFloat(this.tableData[i].clock_in_latitude)
                this.markDefault.lng = parseFloat(this.tableData[i].clock_in_longitude)
                this.markers.push({
                    position: marker
                });
                this.mapMark = 1;
            }
            for (var i = 0; i < this.tableData.length; i++) {
                const marker = {
                    lat: parseFloat(this.tableData[i].clock_out_latitude),
                    lng: parseFloat(this.tableData[i].clock_out_longitude),
                    name: this.tableData[i].name + ', ' + this.tableData[i].clock_out + ' (CLOCK OUT)',
                    icon: this.orangeIcon
                };
                this.markers.push({
                    position: marker
                });
            }
        },
        async getId(id) {
            await this.$store.commit(mutation.SET_LOADING, true);
            await this.$store.dispatch(action.TRACKING_ATTENDANCE, id.id);
            await this.attendance;
            if (this.attendance.length == 0) {
                swal('This user does not have any tracking yet', '', '')
            } else {
                this.markers = [];
                this.path = [];
                this.window_open = false;
                for (var i = 0; i < this.attendance.length; i++) {
                    const marker = {
                        lat: parseFloat(this.attendance[i].latitude),
                        lng: parseFloat(this.attendance[i].longitude),
                        name: id.name + ', ' + this.attendance[i].created_at,
                        icon: this.blueDot
                    };
                    this.markDefault.lat = parseFloat(this.attendance[i].latitude)
                    this.markDefault.lng = parseFloat(this.attendance[i].longitude)
                    this.markers.push({
                        position: marker
                    });
                    this.path.push({
                        lat: parseFloat(this.attendance[i].latitude),
                        lng: parseFloat(this.attendance[i].longitude)
                    });
                    this.mapLine = 1;
                    this.mapMark = 1;
                }
            }
            var self = this;
            setTimeout(function () {
                self.$refs.topside.scrollTop = 0;
            }, 900);
        },
        indexMethod(index) {
            return index + this.paginator.fromP;
        },
    },
    async created() {
        await this.$store.dispatch(action.LIST_ATT_EMPLOYEE, this.currentPage);
        await this.listData
        this.markers = [];
        this.path = [];
        this.window_open = false;
        for (var i = 0; i < this.listData.length; i++) {
            const marker = {
                lat: parseFloat(this.listData[i].clock_in_latitude),
                lng: parseFloat(this.listData[i].clock_in_longitude),
                name: this.listData[i].name + ', ' + this.listData[i].clock_in + ' (CLOCK IN)',
                icon: this.redIcon
            };
            this.markDefault.lat = parseFloat(this.listData[i].clock_in_latitude)
            this.markDefault.lng = parseFloat(this.listData[i].clock_in_longitude)
            this.markers.push({
                position: marker
            });
            this.mapMark = 1;
        }
        for (var i = 0; i < this.listData.length; i++) {
            const marker = {
                lat: parseFloat(this.listData[i].clock_out_latitude),
                lng: parseFloat(this.listData[i].clock_out_longitude),
                name: this.listData[i].name + ', ' + this.listData[i].clock_out + ' (CLOCK OUT)',
                icon: this.orangeIcon
            };
            this.markers.push({
                position: marker
            });
        }
    },
}
